<div class="container-fluid flex-grow-1 container-p-y">    <div class="row">        <div class="d-flex col-xl-12 align-items-stretch">            <!-- Content -->            <div class="card w-100 mb-4">                <div class="card-header with-elements">                    <h5 class="card-header-title mr-2 m-0"><?php echo $pageTitle; ?></h5>                    <?php if ($this->authenticate->isSuperAdmin()) : ?>                        <div class="card-header-elements ml-md-auto">                            <a href="<?php echo base_url("user/create_admin_user") ?>"                               class="btn btn-xs btn-outline-primary">                                <span class="ion ion-md-add"></span> Add User</a>                        </div>                    <?php endif; ?>                </div>                <div class="card-body" style="overflow-x: scroll; padding-right:10px;">                    <?php if($this->authenticate->isSuperAdmin() || $this->authenticate->isAdmin()) : ?>                        <?php echo form_open(base_url("user/all"), array('method' => 'get')) ?>                        <div class="input-group mb-5">                                                        <input type="text" class="form-control" placeholder="Search users..." name="query">                            <span class="input-group-append">                                <button class="btn btn-secondary" type="submit">Filter</button>                            </span>                        </div>                        <?php form_close() ?>                    <?php endif; ?>                    <table class="table card-table m-0">                        <thead>                        <tr>                            <th>#</th>                            <th>Username (Email)</th>                            <th>First Name</th>                            <th>Last Name</th>                                                       <th>Account Type</th>                            <th>Status</th>                            <th>Action</th>                        </tr>                        </thead>                        <tbody>                        <?php foreach ($users as $user) {                            $mainSubscriber = $this->authenticate->isMainSubscriber();                            ?>                            <tr>                                <td><?php echo @$offset += 1; ?></td>                                <td><?php echo htmlspecialchars($user->email); ?></td>                                <td><?php echo htmlspecialchars($user->first_name); ?></td>                                <td><?php echo htmlspecialchars($user->last_name); ?></td>                                <td>                                    <span class="badge badge-secondary"><?php echo ($user->super_admin == 1) ? "Super Admin" : htmlspecialchars($user->account_type); ?>                                    </span>                                    <span class="badge badge-default"><?php echo ($user->account_type_id == 3 && $user->admin == 1) ? "Administrator" : ""; ?></span>                                </td>                                <td><?php echo activeBadge($user->activated); ?></td>                                <td>                                                                       <?php if ($this->authenticate->isSuperAdmin() || $this->authenticate->isAdmin()) : ?>                                        <a class="btn btn-xs icon-btn btn-outline-secondary borderless" href="<?php echo base_url('user/edit/' . $user->user_id); ?>" title="Edit"><i class="ion ion-ios-create"></i></a>                                                                           <?php endif; ?>                                    <?php if ((($user->super_admin == 0) && $this->authenticate->isSuperAdmin())): ?>                                        <a class="btn btn-xs icon-btn btn-outline-danger borderless remove account-confirm" name="<?php echo htmlspecialchars($user->first_name); ?>" href="<?php echo base_url('user/remove/' . $user->user_id); ?>" title="Remove"><i class="ion ion-md-close"></i></a>                                    <?php endif; ?>                                </td>                            </tr>                        <?php } ?>                        </tbody>                    </table>                </div>            </div>            <!-- /Content -->        </div>    </div></div><script type="text/javascript">    $(function () {        $('.account-confirm').on('click', function (e) {            e.preventDefault();            var name = $(this).attr('name');            var location = $(this).attr('href');            bootbox.dialog({                title: 'Are you sure you want to delete ' + name + '\'s account?',                message: 'Do know that deleted ' + 'account cannot be recovered. ' +                'Please only proceed if you\'re sure of deleting this account.',                className: 'bootbox-lg',                buttons: {                    success: {                        label: 'Cancel',                        className: 'btn-success',                        callback: function () {                        },                    },                    main: {                        label: 'Delete',                        className: 'btn-danger',                        callback: function () {                            bootbox.confirm({                                message: 'Are you sure you want to proceed with deleting  ' + name + '\'s account?',                                className: 'bootbox-sm',                                callback: function(result) {                                    if (result === true) {                                        window.location.href = location;                                    }                                },                            });                        },                    }                },            });        });    });</script>